// src/components/UserInputForm.js
import React, { useState } from 'react';

const UserInputForm = ({ onGenerate, onPreview, onExportPDF, onExportExcel }) => {
  const [inputData, setInputData] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    onGenerate(inputData);
  };

  return (
    <div className="container">
      <form onSubmit={handleSubmit} className="chat-box mb-4">
        <div className="form-group">
          <label>You just need to explain your requirement in your natural language and we'll generate on-demand reports for you</label>
          <div class="text-box-wrapper">
          <textarea class="text-area" placeholder="Start writing your requirements..." 
          value={inputData}
          onChange={(e) => setInputData(e.target.value)}></textarea>
          <button type="submit" class="right-arrow-btn">&#9654;</button>
          </div>
        </div>
      </form>
      <div className="d-flex justify-content-center mb-4">
      <button class="preview-button"  onClick={onPreview}>
        <i class="icon">üëÅÔ∏è</i> Preview
       </button>
        <button className="btn btn-info mr-2">
          Preview
        </button>
        <button class="export-button"  onClick={onExportPDF}>
        <i class="icon">üìÑ</i> Export to PDF
        </button>
        <button class="export-button excel-button"  onClick={onExportExcel}>
        <i class="icon">üìä</i> Export to Excel
        </button>
      </div>
    </div>
  );
};

export default UserInputForm;
