// src/App.js
import React, { useState } from 'react';
import UserInputForm from './components/UserInputForm';
import Footer from './components/Footer';
import jsPDF from 'jspdf';
import { utils, writeFile } from 'xlsx';
import 'bootstrap/dist/css/bootstrap.min.css';
import * as XLSX from 'xlsx';

const App = () => {
  const [report, setReport] = useState(null);

  const generateReport = async (inputData) => {
      console.log(inputData);
      const response = await fetch('https://elevatu.pythonanywhere.com/?input=' + inputData)
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.text();
      })
      .then(text => {
        text = text.replace(/'/g, '"');
        const jsonObject = JSON.parse(text);
        console.log(jsonObject);
        exportAsExcel(jsonObject);
      })
      .catch(error => {
        console.error('There was a problem with the fetch operation:', error);
      });
  };

  const previewReport = () => {
    if (report) {
      alert(`Previewing report: ${report}`);
    } else {
      alert('Please generate a report first.');
    }
  };

  const exportAsPDF = () => {
    if (report) {
      const doc = new jsPDF();
      doc.text(report, 10, 10);
      doc.save('report.pdf');
    } else {
      alert('Please generate a report first.');
    }
  };

  const exportAsExcel = (jsonData) => {
    // Convert JSON data to worksheet
    const ws = XLSX.utils.json_to_sheet(jsonData);
    // Create a new workbook
    const wb = XLSX.utils.book_new();
    // Append the worksheet to the workbook
    XLSX.utils.book_append_sheet(wb, ws, 'Sheet1');
    XLSX.writeFile(wb, 'data.xlsx');
  };

  return (
    <div className="d-flex flex-column min-vh-100" style={{ backgroundColor: '#AAD7D9' }}>
      <div className="container text-center py-5">
        <h1>ReportXpress</h1>
        <UserInputForm
          onGenerate={generateReport}
          onPreview={previewReport}
          onExportPDF={exportAsPDF}
          onExportExcel={exportAsExcel}
        />
        {report && (
          <div className="mt-4">
            <h2>Generated Report</h2>
            <p>{report}</p>
          </div>
        )}
      </div>
      <Footer />
    </div>
  );
};

export default App;
